$('select#champ').change(function() {
	var filter = $(this).val()
	filterList(filter);
});
function filterList(value) {
	var list = $(".champ-list .champ-item");
	$(list).fadeOut("fast");
	if (value == "All") {
		$(".champ-list").find("article").each(function (i) {
			$(this).delay(200).slideDown("fast");
		});
	} else {
		$(".champ-list").find("article[data-category*=" + value + "]").each(function (i) {
			$(this).delay(200).slideDown("fast");
		});
	}
}

