var TablPicasso = document.getElementsByClassName('Picasso');
var TablRenoir = document.getElementsByClassName('Renoir');
var TablMonet = document.getElementsByClassName('Monet');
var tousClic = document.getElementById('filtre_tous');
var FiltrePicasso = document.getElementById('filtre_picasso');
var FiltreRenoir = document.getElementById('filtre_renoir');
var FiltreMonet = document.getElementById('filtre_monet');
/* ------------------- TOUS ------------------- */
document.getElementById('filtre_tous').onclick = function (){
 	for(var i = 0; i < TablRenoir.length; i++){
		TablRenoir[i].classList.remove('hidden'); 
	}
	for(var i = 0; i < TablMonet.length; i++){
		TablMonet[i].classList.remove('hidden'); 
	}
	for(var i = 0; i < TablPicasso.length; i++){
		TablPicasso[i].classList.remove('hidden'); 
	}
	tousClic.classList.add('clic');
	FiltrePicasso.classList.remove('clic');
	FiltreRenoir.classList.remove('clic');
	FiltreMonet.classList.remove('clic');
}
/* ------------------ Picasso ----------------- */
document.getElementById('filtre_picasso').onclick = function (){
 	for(var i = 0; i < TablRenoir.length; i++){
		TablRenoir[i].classList.add('hidden'); 
	}
	for(var i = 0; i < TablMonet.length; i++){
		TablMonet[i].classList.add('hidden'); 
	}
	for(var i = 0; i < TablPicasso.length; i++){
		TablPicasso[i].classList.remove('hidden'); 
	}
	FiltrePicasso.classList.add('clic');
	FiltreRenoir.classList.remove('clic');
	FiltreMonet.classList.remove('clic');
	tousClic.classList.remove('clic');
}
/* ------------------- Renoir ------------------ */
document.getElementById('filtre_renoir').onclick = function (){
	for(var i = 0; i < TablRenoir.length; i++){
		TablRenoir[i].classList.remove('hidden'); 
	}
	for(var i = 0; i < TablMonet.length; i++){
		TablMonet[i].classList.add('hidden'); 
	}
	for(var i = 0; i < TablPicasso.length; i++){
		TablPicasso[i].classList.add('hidden'); 
	}
	tousClic.classList.remove('clic');
	FiltrePicasso.classList.remove('clic');
	FiltreRenoir.classList.add('clic');
	FiltreMonet.classList.remove('clic');
}
/* ------------------- Monet ------------------- */
document.getElementById('filtre_monet').onclick = function (){

	for(var i = 0; i < TablMonet.length; i++){
		TablMonet[i].classList.remove('hidden'); 
	}
	for(var i = 0; i < TablPicasso.length; i++){
		TablPicasso[i].classList.add('hidden'); 
	}
	for(var i = 0; i < TablRenoir.length; i++){
		TablRenoir[i].classList.add('hidden'); 
	}
	tousClic.classList.remove('clic');
	FiltrePicasso.classList.remove('clic');
	FiltreRenoir.classList.remove('clic');
	FiltreMonet.classList.add('clic');
}

