.photo_item {
    position: relative;
    overflow: hidden;
    display: block;
		width: 20%;
		height:250px;
		margin: 0 35px; 
		float: left;
	padding-top: 90px;
	margin-top: 20px;
}

.photo_item .photo_item_hover {
    position: absolute;
    top: 0px;
    left: 0px;
    height: 100%;
    width: 80%;
    background-color: rgba(0, 0, 0, .8);
    -webkit-transform: translate(-100%);
    transform: translate(-100%);
    opacity: 0;
    -webkit-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}

.photo_item .photo_item_hover .item_info {
    text-align: center;
    position: absolute;
    top: 50%;
    left: 50%;
    -webkit-transform: translate(-50%, -50%);
    transform: translate(-50%, -50%);
    padding: 10px;
    width: 100%;
    font-weight: bold;
}

.photo_item .photo_item_hover .item_info span {
    display: block;
    color: white;
    font-size: 18px;
    -webkit-transform: translateX(-100px);
    transform: translateX(-100px);
    -webkit-transition: all .2s ease-in-out .2s;
    transition: all .2s ease-in-out .2s;
    opacity: 0;
}


.photo_item:hover .photo_item_hover {
    opacity: 1;
    -webkit-transform: translateX(0);
    transform: translateX(0);
}


.phto_item:hover .item_info{
    opacity: 1;
    -webkit-transform: translateX(0);
    transform: translateX(0);
}

