(function () {
  var allButton = document.getElementById('all'); 
  var list1Button = document.getElementById('list1'); 
  var list2Button = document.getElementById('list2');
  var list3Button = document.getElementById('list3');

  var showItems = function (items) {
    for (var i=0; i<items.length; i++) {
      items[i].style.display = "inline-block";
    }
  };
  var hideItems = function (items) {
    for (var i=0; i<items.length; i++) {
      items[i].style.display = "none";
    }
  };

  list1Items = document.getElementsByClassName('list1');
  list2Items = document.getElementsByClassName('list2');
  list3Items = document.getElementsByClassName('list3');

  allButton.onclick = function() {
    showItems(list1Items);
    showItems(list2Items);
    showItems(list3Items);
  };
  list1Button.onclick = function() {
    showItems(list1Items);
    hideItems(list2Items);
    hideItems(list3Items);
    };
  list2Button.onclick = function() {
    hideItems(list1Items);
    hideItems(list3Items);
    showItems(list2Items);
    };
  list3Button.onclick = function() {
    hideItems(list1Items);
    hideItems(list2Items);
    showItems(list3Items);
    };
  })();

