var products = document.querySelectorAll("#products > article");

function display(element)
{
	if (element.id == "all")
	{
		for (var i = 0; i < products.length; i++)
		{
			products[i].style.display = "block";
		}
	}
	else
	{
		for (var i = 0; i < products.length; i++)
		{
			if (products[i].classList.contains(element.id))
				products[i].style.display = "block";
			else
				products[i].style.display = "none";
		}		
	}


}

